INSERT INTO `news` (`id`, `title`, `hash_tag`, `alias`, `image_url`, `introtext`, `fulltext`, `state`, `language_id`, `enabled`, `created_date`) VALUES
(1, 'Một ngày làm cư dân làng chài Thanh Nam', '', 'mot-ngay-lam-cu-dan-lang-chai-thanh-nam', '', '<p style="text-align: justify;">Tour “Một ngày làm ngư dân phố Hội” lại đưa du khách về với làng quê nhiều cổ tích Thanh Nam – Cồn Chài. Tại đây, du khách sẽ được những chiếc thuyền con của ngư dân đưa đi tham quan phong cảnh vùng sông nước hữu tình, được chỉ dẫn cách chèo thuyền, giăng lưới, thả rập, cất rớ, vải chài bắt cá tôm… Một quán ăn ven sông đã chuẩn bị sẵn bếp than hồng để du khách được chế biến các sản phẩm mà mình đánh bắt được trên sông, nhắm nháp với ly rượu đồng quê, uống gáo nước chè tươi đậm đà hương vị, cùng ngư dân đan vá lưới và hát hò, tán chuyện. Trước khi kết thúc tour, du khách sẽ được xuôi thuyền về Cửa Đại để chụp nhìn, ngắm cảnh hoàng hôn trên biển hoặc đến rừng dừa Bảy Mẫu, thăm khu du lịch sinh thái Thuận Tình.</p>\n<p style="text-align: justify;"><strong><img style="padding: 0px; margin: 0px;" alt="langchaithanhnam" height="516" width="690" src="images/stories/langchaithanhnam.jpg" /></strong></p>\n<p style="text-align: justify;"><strong>Chương trình:</strong></p>\n<p style="text-align: justify;">7g30: Hướng dẫn viên đón quý khách tại điểm hẹn, đưa đến nhà một hộ dân ở làng chài Thanh Nam.<br />8h00 – 11g00: Du khách cùng ngư dân đi làm nghề lưới bén bằng thuyền, học và làm rớ chồ.<br />11g00 – 14g00: Du khách cùng gia đình làm cơm và dùng cơm trưa với các sản phẩm vừa đánh bắt được trên sông.<br />14g00 – 16g30: Du khách học vá lưới, theo ngư dân đi vải chài đánh bắt cá trên sông.<br />16g30: Du khách tạm biệt gia đình, theo hướng dẫn viên trở về điểm hẹn.</p>\n<p style="text-align: justify;"><strong>Giá tour:</strong></p>\n<p style="text-align: justify;">Từ 18 USD – 30 USD/ khách (tuỳ theo số lượng khách tham gia) bao gồm: phương tiện vận chuyển bằng xe đạp và thuyền, hướng dẫn viên, các hoạt động theo chương trình và ăn trưa. Trẻ em dưới 12 tuổi được giảm 50% giá tour.Làng chài Thanh Nam hay còn gọi là Cồn Chài Thanh Nam được hình thành bởi phù sa sông Thu Bồn. Người làm nghề trên sông đến đây vãi chài, đánh bắt cá, tôm, cua, neo đậu ghe thuyền …. Dần dần thấy thuận tiện nên an cư lập nghiệp và tên gọi Cồn Chài có từ đấy.</p>', '', 1, 1, 1, '2009-08-15 01:02:51'),
(2, 'Một ngày làm cư dân làng rau Trà Quế', '', 'mot-ngay-lam-cu-dan-lang-rau-tra-que', '', '<p style="text-align: justify;">Rau thơm Trà Quế đã vang danh khắp chốn từ lâu. Một ngày đến với vùng quê thôn dã này, du khách sẽ được những nông dân “hiền như đất” đón tiếp nồng hậu, hướng dẫn cách cuốc đất, vun luống, đánh hàng, chọc lổ, gieo hạt, trồng từng loại rau và cùng xuống đầm vớt rong về bón cho rau hoặc hái rau từ những thửa, luống mà du khách đến trước đã gieo trồng. Đến trưa, du khách theo gia chủ về nhà chế biến và thưởng thức những món ăn đặc sản từ rau Trà Quế, rồi mắc võng hoặc nằm trên những chõng tre ngoài vườn để nghỉ ngơi, hàn huyên, thư thái đọc sách trong một không gian tĩnh lặng của miền quê bình dị. Chiều giăng câu, bủa lưới, cất vó bắt cá tôm… để rồi khi chia tay vẫn còn bịn rịn, nhớ thương cùng những ấn tượng khó phai.</p>\r\n<p style="text-align: justify;"><img alt="lang-rau-tra-que-hoi-an" src="images/stories/lang-rau-tra-que-hoi-an.jpg" height="473" width="690" /></p>\r\n<strong>\r\n<p style="text-align: justify;">Chương trình:</p>\r\n</strong>\r\n<p style="text-align: justify;">7g30: Hướng dẫn viên đón quý khách tại điểm hẹn, đưa đến một hộ dân ở làng rau Trà Quế.<br />8g00 – 11g00: Du khách tham quan vườn rau, cùng nông dân tưới nước, đi lấy rong ở đầm Trà Quế về bón cho rau.<br />11g00 – 14g00: Du khách cùng gia đình làm cơm và dùng cơm trưa với các món ăn được chế biến từ rau trong vườn.<br />14g00 – 17g00: Du khách học cách làm đất, gieo hạt giống và thu hoạch rau.<br />17g00: Du khách tạm biệt gia đình, theo hướng dẫn viên trở về điểm hẹn.</p>\r\n<p style="text-align: justify;"><strong>Giá tour:</strong></p>\r\n<p style="text-align: justify;">Từ 14 USD – 25 USD/ khách (tuỳ theo số lượng khách tham gia) bao gồm: phương tiện vận chuyển bằng xe đạp, hướng dẫn viên, các hoạt động theo chương trình và ăn trưa. Trẻ em dưới 12 tuổi được giảm 50% giá tour.</p>\r\n<p><strong>Liên hệ tư vấn tour:</strong><br /> <strong><span style="color: #666666;"><span style="font-family: Arial;">Địa chỉ:</span></span></strong> <span style="color: #666666;"><span style="font-family: Arial;">Lô B2-09-10, đường Loseby, An   Cư 3, Quận Sơn Trà, TP Đà Nẵng</span></span><span style="color: #666666;"><span style="font-family: Arial;"><strong> </strong></span></span><br /> <strong><span style="color: #666666;"><span style="font-family: Arial;">Di động:</span></span></strong> <span style="color: #666666;"><span style="font-family: Arial;">0988 331 375</span></span><br /> <strong><span style="color: #666666;"><span style="font-family: Arial;">Email:</span></span></strong> <span style="color: #666666;"><span style="font-family: Arial;"> <a href="mailto:Pearlseahotel@gmail.com">Phanquockhuong@gmail.com</a></span></span><br /> <strong><span style="color: #666666;"><span style="font-family: Arial;">Yahoo:</span></span></strong> <span style="color: #666666;"><span style="font-family: Arial;">Khuongquocphan</span></span><br /> <strong><span style="color: #666666;"><span style="font-family: Arial;">Skype:</span></span></strong> <span style="color: #666666;"><span style="font-family: Arial;">Phanquockhuong</span></span></p>', '', 1, 1, 1, '2009-08-15 01:20:04'),
(3, 'Trợ giúp', '', 'tro-giup', '', '<p><strong>1. Lỗi không hiển thị VR360 khi thiếu Flash Player</strong></p>\r\n<p>Nếu máy tính Quý khách không hiển thị VR360 xin vui lòng click vào liên kết sau: <a href="http://www.adobe.com/go/getflashplayer"><strong>Flash Player</strong></a> và chọn <strong>Agree and install now</strong>.</p>\r\n<p>- Nếu sử dụng Firefox hãy lưu file về máy tính, đóng trình duyệt và chạy file cài đặt mới tải về.</p>\r\n<p>- Nếu sử dụng Internet Explore màn hình sẽ hiển thị thông báo sau:</p>\r\n<p><img border="0" width="521" src="images/stories/gold_bar.gif" alt="gold_bar" height="115" /></p>\r\n<p>Hãy nhắp chuột phải vào cửa sổ màu vàng trên và chọn <strong>Instal ActiveX Control</strong>.</p>\r\n<p><strong>2. Lỗi không hiển thị VR360 khi cài đặt Internet Download Manager</strong></p>\r\n<p>Chạy <strong>Internet Download Manager</strong> chọn <strong>Option | File types</strong></p>\r\n<p><strong></strong><img width="350" src="images/stories/option_idm.jpg" alt="option_idm" height="426" /></p>\r\n<p>Tại ô <strong>Don''t start downloading automatically from the following site:</strong> gõ vào <strong>&lt;ký tự trống (space-bar)&gt;  *.vr360.vn</strong> và chọn <strong>OK</strong></p>', '', 1, 1, 1, '2009-09-09 06:55:45'),
(4, 'Khám phá Cù Lao Chàm - Đảo xanh quyến rũ', '', 'kham-pha-cu-lao-cham-dao-xanh-quyen-ru', '', '<p style="text-align: justify;">Làm cuộc hành trình đến với đảo xanh bằng tàu du lịch, quý khách sẽ được khám phá một vùng thiên nhiên hoang sơ, thơ mộng tại hòn Lao, hòn Dài, hòn Chồng, hòn Yến … được hoà mình vào cuộc sống dân dã trong sự đón tiếp nồng hậu của cư dân các làng chài, được chiêm ngưỡng vẻ đẹp quyến rũ của các địa danh mang nhiều huyền thoại như bãi Hương, bãi Làng, bãi Chồng, hang Bà, Âu thuyền, chùa Hải Tạng; được thưởng thức các loại đặc sản đậm đà hương vị biển Cù Lao Chàm như: tôm, cá, mực, vú nàng vú xao, cua đá, rau rừng …</p>\r\n<p style="text-align: justify;"><img alt="bien-cu-lao-cham" src="images/stories/bien-cu-lao-cham.jpg" height="600" width="800" /></p>\r\n<p style="text-align: justify;">Tại đây, du khách còn được tận hưởng những cảm giác thú vị của mùa hè, được thỏa thích đùa vui cùng sóng biển, tắm nắng trên những bãi cát mịn màng, chinh phục những ngọn đồi hùng vĩ, tham gia các hoạt động thể thao: mô tô nước, dù bay, lướt ván, bơi thuyền Kayak hay lặn thám hiểm các hang đá nằm sâu dưới lòng đại dương cùng hàng trăm loài cá, loài san hô lấp lánh muôn sắc màu của miền nhiệt đới. Ngoài ra, du khách còn có thể tham quan đảo Yến, câu cá và tham gia vào đêm sinh hoạt lửa trại với nhiều trò chơi dân gian vui nhộn.</p>\r\n<h4>Chương trình 1: 2 ngày 1 đêm</h4>\r\n<p style="text-align: justify;">Giá tour: Từ 16 USD – 20 USD/ 1khách (tuỳ theo số lượng khách tham gia)<br />Bao gồm: phương tiện vận chuyển bằng thuyền (nếu du khách yêu cầu đi tàu cao tốc trả thêm 5 USD), bảo hiểm, hướng dẫn viên, các hoạt động theo chương trình và ăn, ngủ (không bao gồm phí tổ chức sinh hoạt lửa trại, dịch vụ lặn biển, các trò chơi trên biển ngoài chương trình).</p>\r\n<p style="text-align: justify;">- Trẻ em dưới 12 tuổi được giảm 50% giá tour</p>\r\n<p style="text-align: justify;"><strong>Ngày 1:</strong></p>\r\n<p style="text-align: justify;">07g00: Hướng dẫn viên đón du khách tại điểm hẹn và đưa đến bến để lên thuyền đi Cù Lao Chàm. Trên thuyền, du khách ngồi ngắm phong cảnh sông nước, làng quê Hội An dọc hai bên bờ sông Thu Bồn.</p>\r\n<p style="text-align: justify;">Qua Cửa Đại thuyền sẽ đưa du khách đến đảo Cù Lao Chàm. Tại đây, du khách tham quan làng cá, chùa Hải Tạng và Âu Thuyền (nơi tránh bão của tàu thuyền), ăn trưa, tự do nghỉ ngơi, tắm biển, câu cá, tham gia các trò chơi trên biển, sinh hoạt lửa trại (nếu yêu cầu) và nghỉ đêm tại Bãi Chồng hoặc Bãi Ông..</p>\r\n<p style="text-align: justify;"><strong>Ngày 2:</strong></p>\r\n<p style="text-align: justify;">06g00: Tắm biển, ăn sáng sau đó thuyền sẽ đưa du khách đến bãi Hương tham quan miếu Ông tổ nghề Yến, chùa Ngọc Hương và tham quan đảo Yến (nhìn từ xa) .<br />10g00: Du khách lên thuyền về lại Hội An. Hướng dẫn đưa du khách về lại điểm hẹn.</p>\r\n<h4>Chương trình 2: đi và về trong ngày</h4>\r\n<p style="text-align: justify;">Giá tour: Từ 8 USD – 12 USD/ 1khách (tuỳ theo số lượng khách tham gia)<br />Bao gồm: phương tiện vận chuyển bằng thuyền (nếu du khách yêu cầu đi tàu cao tốc thì trả thêm 5 USD, bảo hiểm, hướng dẫn viên, các hoạt động theo chương trình và ăn trưa, ăn nhẹ trên thuyền (không bao gồm phí các dịch vụ lặn biển, các trò chơi trên biển ngoài chương trình).</p>\r\n<p style="text-align: justify;">- Trẻ em dưới 12 tuổi được giảm 50% giá tour.</p>\r\n<p style="text-align: justify;">07g00: Hướng dẫn viên đón du khách tại điểm hẹn và đưa đến bến để lên thuyền đi <span style="color: #999999;"><a target="_blank" title="tour du lich cu lao cham" href="http://www.tourculaocham.net">Cù Lao Chàm</a>.</span> Trên thuyền, du khách ngồi ngắm phong cảnh làng quê Hội An dọc hai bờ sông Thu Bồn. Thuyền sẽ đưa du khách vượt cửa biển Cửa Đại để đến đảo Cù Lao Chàm. Tại đây, du khách tham quan làng cá, chùa Hải Tạng, Âu Thuyền; tự do nghỉ ngơi, câu cá, tắm biển và ăn trưa tại bãi Chồng.</p>\r\n<p style="text-align: justify;">15g00: Du khách lên thuyền về lại Hội An. Hướng dẫn viên đưa du khách về lại điểm hẹn.</p>\r\n<p style="text-align: justify;"> </p>\r\n<p><strong>Liên hệ tư vấn tour:</strong><br /> <strong><span style="color: #666666;"><span style="font-family: Arial;">Địa chỉ:</span></span></strong> <span style="color: #666666;"><span style="font-family: Arial;">Lô B2-09-10, đường Loseby, An   Cư 3, Quận Sơn Trà, TP Đà Nẵng</span></span><span style="color: #666666;"><span style="font-family: Arial;"><strong> </strong></span></span><br /> <strong><span style="color: #666666;"><span style="font-family: Arial;">Di động:</span></span></strong> <span style="color: #666666;"><span style="font-family: Arial;">0988 331 375</span></span><br /> <strong><span style="color: #666666;"><span style="font-family: Arial;">Email:</span></span></strong> <span style="color: #666666;"><span style="font-family: Arial;"> <a href="mailto:Pearlseahotel@gmail.com">Phanquockhuong@gmail.com</a></span></span><br /> <strong><span style="color: #666666;"><span style="font-family: Arial;">Yahoo:</span></span></strong> <span style="color: #666666;"><span style="font-family: Arial;">Khuongquocphan</span></span><br /> <strong><span style="color: #666666;"><span style="font-family: Arial;">Skype:</span></span></strong> <span style="color: #666666;"><span style="font-family: Arial;">Phanquockhuong</span></span></p>\r\n<p style="text-align: justify;">  Normal 0     false false false  EN-US X-NONE X-NONE              MicrosoftInternetExplorer4              </p>', '', 1, 1, 1, '2009-08-15 00:54:59'),
(5, 'Tham quan thực tế ảo Pearl Sea Hotel', '', 'tham-quan-thuc-te-ao-pearl-sea-hotel', '', '<div>Quý khách có thể tương tác bằng cách click và giữ chuột vào hình để kéo thay đổi hướng nhìn. Nhấn Shift hoặc Ctrl để Zoom. Hoặc sử dụng các nút điều kiển ở menu bên phải để thay đổi hướng nhìn.</div>\r\n<object data="http://www.danang360.com/danangflash/vr360.swf" type="application/x-shockwave-flash" height="500" width="680">\r\n<param name="flashvars" value="base=LC_Bien_PVD/&amp;lwImg=resources/lc_bien_pvd_vr360.jpg&amp;iniFile=config_LC_Bien_PVD.bin&amp;progressType=1&amp;swfFile=resources/lw_sceneviewer.swf&amp;lwBgColor=255,0,0,0&amp;lwBarBgColor=255,255,255,255&amp;lwBarColor=255,0,102,255&amp;lwBarBounds=225,300,230,65&amp;lwlocation=0&amp;lwShowLoadingPercent=true&amp;lwTextColor=255,255,255,255" />\r\n<param name="allownetworking" value="all" />\r\n<param name="allowscriptaccess" value="always" />\r\n<param name="wmode" value="transparent" />\r\n<param name="allowfullscreen" value="true" />\r\n<param name="scale" value="noscale" />\r\n<param name="src" value="http://www.danang360.com/danangflash/vr360.swf" />\r\n</object>\r\n<div>Vui lòng xem hướng dẫn tại mục <a href="index.php?option=com_content&amp;view=article&amp;id=87:tro-giup&amp;catid=42">Trợ giúp</a> nếu máy tính Quý khách không hiển thị VR360.</div>', '', 1, 1, 1, '2009-06-29 03:23:57'),
(6, 'Giới thiệu Pearl Sea Hotel - Khách sạn Biển Ngọc', '', 'gioi-thieu-pearl-sea-hotel-khach-san-bien-ngoc', '', '<p style="text-align: justify;"><img style="float: left; margin-right: 5px; margin-bottom: 5px;" alt="Giới thiệu Pearl Sea Hotel" src="images/stories/khachsanvenbien.jpg" />Tọa lạc trên một vị trí lý tưởng,bên cạnh bãi biển Mỹ Khê xinh đẹp,nơi được tạp chí Forbes bình chọn là một trong sáu bãi biển đẹp nhất hành tinh.  Khách sạn Pearl Sea luôn là sự lựa chọn tuyệt vời của Quý khách. Đến với khách sạn Pearl Sea, Quý khách có đầy đủ không gian tiện lợi để ngắm bán đảo Sơn Trà thơ mộng bên cạnh bãi biển trong xanh, rực ánh nắng vàng, ngắm cảnh toàn thành phố Đà Nẵng, ngắm cầu quay Sông Hàn xinh đẹp, cầu Thuận Phước lấp lánh  dưới ánh đèn màn đêm huyền ảo.</p>\r\n<p style="text-align: justify;">Khách sạn Pearl Sea là một trong những khách sạn mới bên cạnh biển Mỹ Khê, khai trương vào tháng 04 năm 2011, được thiết kế theo tiêu chuẩn tương đương 3 sao gồm 10 tầng - 45 phòng cùng những trang thiết bị hiện đại, sang trọng và ấm cúng. Với đội ngũ nhân viên trẻ trung , năng động và nhiệt tình, Khách sạn Pearl Sea tự tin sẽ mang lại cho Quý khách sự hài lòng cao nhất.</p>\r\n<a class="readon" href="index.php?option=com_content&amp;view=article&amp;id=133&amp;Itemid=86">Xem chi tiết...</a>', '', 1, 1, 1, '2009-06-29 03:23:57'),
(7, 'Tham quan phố cổ Hội An', '', 'tham-quan-pho-co-hoi-an', '', '<p style="text-align: justify;">Nằm tiếp giáp đường Nguyễn Thị Minh Khai và đường Trần Phú – Hội An, Chùa Cầu (hay còn gọi chùa Nhật Bản) là công trình kiến do các thương gia Nhật Bản đến buôn bán tại Hội An xây dựng vào khoảng giữa thế kỷ 16. Do ảnh hưởng của thiên tại địch hoạ, Chùa Cầu đã qua nhiều lần trùng tu và dần mất đi các yếu tố kiến trúc Nhật Bản, thay vào đó là kiến trúc mang đậm phong cách Việt, Trung.</p>\r\n<p style="text-align: justify;"><img alt="chua-cau" height="535" width="800" src="images/stories/chua-cau.JPG" /></p>\r\n<p style="text-align: justify;">Chùa Cầu có dáng hình chữ Công, mặt cầu bằng ván gỗ cong vòng ở giữa, bắt qua con lạch thông ra sông Hoài. Cầu có mái che uốn cong mềm và được chạm trổ nhiều hoạ tiết tinh xảo. Trên cửa chính của Chùa Cầu có chạm nổi 3 chữ Hán là Lai Viễn Kiều (có nghĩa là cầu của những người bạn từ xa đến) – tên do chúa Nguyễn Phúc Chu đặt trong một lần viếng thăm Hội An vào năm 1719. Trên sườn cầu có một ngôi miếu nhỏ thờ thần Bắc Đế Trấn Vũ – thần chuyên trấn trị phong ba, lũ lụt theo tín ngưỡng của người Trung Hoa. Ở hai đầu cầu có đặt hai nhóm tượng khỉ và chó bằng gỗ ngồi chầu. Lai lịch của Chùa Cầu gắn liền với truyền thuyết về con Cù – một loại thuỷ quái có đầu nằm ở Ấn Độ, mình ở Việt Nam và phần đuôi ở tận Nhật Bản và mỗi lần Cù cựa quậy là gây ra lũ lụt, động đất ở những nơi này. Vì vậy, ngoài việc xây cầu để phục vụ giao thông, người xưa còn có hàm ý trấn yểm loài thuỷ quái, giữ cho cuộc sống yên bình.<br />Chùa Cầu là tài sản vô giá và đã chính thức được chọn là biểu tượng của Hội An.</p>\r\n<h3>Nhà cổ Quân Thắng (Số 77 Trần Phú – thị xã Hội An)</h3>\r\n<p style="text-align: justify;">Là một trong những nhà cổ được đánh giá là đẹp nhất Hội An hiện nay. Ngôi nhà có niên đại hơn 150 năm, mang phong cách kiến trúc vùng Hoa Hạ Trung Hoa. Qua năm tháng, ngôi nhà vẫn được bảo tồn khá nguyên trạng về kiểu dáng kiến trúc và các bài trí nội thất, giúp ta hình dung được phần nào lối sống của các thế hệ chủ nhân, những người thuộc tầng lớp thương gia ở thương cảng Hội An trước đây. Được biết, toàn bộ phần kiến trúc và điêu khắc gỗ rất sinh động, tinh tế của ngôi nhà này đều do các nghệ nhân làng mộc Kim Bồng thực hiện. Đây là một điểm tham quan chính trong hành trình khám phá di sản văn hoá thế giới Hội An của du khách.</p>\r\n<h3>Nhà cổ Tấn Ký (Số 10. Nguyễn Thái Học – thị xã Hội An)</h3>\r\n<p style="text-align: justify;">Được xây dựng cách đây gần 200 năm, nhà Tấn Ký có kiểu kiến trúc hình ống – đặc trưng của loại nhà phố Hội An, với nội thất chia làm nhiều gian, mỗi gian có chức năng riêng. Mặt tiền nhà là nơi để mở cửa hiệu buôn bán, mặt sau thông với bến sông để làm nơi xuất, nhập hàng hoá. Vật liệu trang trí nội thất ngôi nhà chủ yếu là các loại gỗ quý và được trạm trỗ, điêu khắc rất tinh xảo các hình về giao long, hoa quả, bát bửu, dải lụa … thể hiện sự sung túc của các thế hệ chủ nhân.</p>\r\n<p style="text-align: justify;">Ngày 17 tháng 2 năm 1990, nhà Tấn Ký đã được cấp bằng di tích lịch sử – văn hoá quốc gia.</p>\r\n<h3>Nhà cổ Phùng Hưng (Số 4. Nguyễn Thị Minh Khai – thị xã Hội An)</h3>\r\n<p style="text-align: justify;">Với tuổi thọ hơn 100 năm, nhà Phùng Hưng có kết cấu độc đáo với phần gác cao bằng gỗ và các hành lang rộng bao quanh, thể hiện sự phát triển về kiến trúc và sự giao lưu giữa các phong cách kiến trúc Á Đông tại Hội An trong các thế kỷ trước đây. Ngôi nhà chứa đựng nhiều thông tin về lối sống của tầng lớp các thương nhân ở thương cảng Hội An xưa. Mặc dù cũng được thực hiện bằng chất liệu quý nhưng nhà Phùng Hưng không trạm trỗ, điêu khắc cầu kỳ mà được giữ thô một cách cố ý.</p>\r\n<p style="text-align: justify;">Nhà Phùng Hưng được cấp bằng di tích lịch sử – văn hoá quốc gia vào ngày tháng 6 năm 1993</p>\r\n<h3>Hội quán Phúc Kiến (Số 46. Trần Phú – thị xã Hội An)</h3>\r\n<p style="text-align: justify;">Tương truyền, tiền thân của Hội quán là một gian miếu nhỏ thờ pho tượng Thiên Hậu Thánh Mẫu (bà chúa phù hộ cho thương nhân vượt sóng gió đại dương) vớt được tại bờ biển Hội An vào năm 1697. Qua nhiều lần trùng tu, với sự đóng góp chủ yếu của Hoa Kiều bang Phúc Kiến, hội quán càng trở nên rực rỡ, khang trang góp phần tô điểm diện mạo kiến trúc đô thị cổ Hội An. Thông qua cách bài trí thờ phụng các hình nhân: 6 vị tiền hiền (lục tánh), bà mụ, thần tài … hội quán thể hiện sâu sắc triết lý Á Đông về hạnh phúc con người.</p>\r\n<p style="text-align: justify;">Hàng năm, vào các ngày Nguyên Tiêu (15 tháng Giêng âm lịch), Vía Lục Tánh (16 tháng 2 Âm lịch), vía Thiên Hậu (23tháng 3 âm lịch) … tại hội quán Phúc Kiến diễn ra nhiều hoạt động lễ hội thu hút rất nhiều du khách trong và ngoài nước đến tham gia.</p>\r\n<p style="text-align: justify;">Hội quán Phúc Kiến đã được cấp bằng di tích lịch sử – văn hoá quốc gia ngày 17 tháng 2 năm 1990.</p>\r\n<h3>Hội quán Triều Châu (Số 157. Nguyễn Duy Hiệu – thị xã Hội An)</h3>\r\n<p style="text-align: justify;">Hội quán được Hoa Kiều bang Triều Châu xây dựng vào năm 1845 để thờ Phục Ba tướng quân Mã Viện – vị thần giỏi chế ngự sóng gió giúp cho việc đi lại buôn bán trên biển được thuận buồm xuôi gió, đắc lợi.</p>\r\n<p style="text-align: justify;">Hội quán có giá trị đặc biệt về kết cấu kiến trúc với bộ khung gỗ trạm gỗ chạm trổ tinh xảo, cùng những hoạ tiết, hương án trang trí bằng gỗ và những tác phẩm đắp nổi bằng sành sứ tuyệt đẹp.</p>\r\n<h3>Hội quán Quảng Đông (Số 17. Trần Phú – thị xã Hội An)</h3>\r\n<p style="text-align: justify;">Hội quán được Hoa Kiều bang Quảng Đông xây dựng vào năm 1885, thoạt đầu để thờ Thiên Hậu Thánh Mẫu và Đức Khổng Tử, sau năm 1911 chuyển sang thờ Quan Công và Tiền Hiền của bang.</p>\r\n<p style="text-align: justify;">Với nghệ thuật sử dụng hài hào các chất liệu gỗ, đá trong kết cấu chịu lực và hoạ tiết trang trí đã đem lại cho hội quán vẻ đẹp đường bệ, riêng có. Hàng năm, vào ngày Nguyên Tiêu (15 tháng Giêng Âm lịch), vía Quan Công (24 tháng 6 Âm lịch) tại đây diễn ra lễ hội rất linh đình, thu hút nhiều người tham gia.</p>\r\n<h3 style="text-align: justify;">Hội quán Ngũ Bang (Số 64. Trần Phú – thị xã Hội An)</h3>\r\n<p style="text-align: justify;">Hội quán Ngũ Bang còn có tên là hội quán Dương Thương hay Trung Hoa hội quán, do các thương khách người Hoa gốc Phúc Kiến, Triều Châu, Quảng Đông, Hải Nam, Gia Ứng xây dựng vào năm 1741 để làm nơi thờ tự Thiên Hậu Ngũ Bang mang đậm phong cách kiến trúc Trung Hoa</p>\r\n<h3>Chùa Ông (Số 24. Trần Phú – thị xã Hội An)</h3>\r\n<p style="text-align: justify;">Chùa Ông được xây dựng năm 1653, đã qua 6 lần trùng tu vào các năm: 1753, 1783, 1827, 1864, 1904, 1906. Chùa Ông có kiến trúc uy nghi, hoành tráng, tại đây thờ tượng Quan Vân Trường (một biểu tượng về trung – tín – tiết – nghĩa) nên còn có tên gọi là Quan công Miếu. Chùa Ông đã từng là trung tâm tín ngưỡng của Quảng Nam xưa, đồng thời cũng là nơi các thương nhân thường lưu đến để cam kết trong việc vay nợ, buôn bán, làm ăn và xin xăm cầu may.</p>\r\n<h3>Quan âm Phật tự Minh Hương (Số 7. Nguyễn Huệ – thị xã Hội An)</h3>\r\n<p style="text-align: justify;">Đây là ngôi chùa thờ Phật duy nhất còn lại giữa khu phố cổ, có kiến trúc và cảnh quan xinh đẹp đồng thời còn lưu giữ gần như nguyên vẹn các tác phẩm điêu khắc gỗ đặc sản do các nghệ nhân làng mộc Kim Bồng thực hiện. Chùa thờ Phật Quan Thế Âm Bồ Tát và một số chư vị Phật, Bồ Tát khác, vì vậy trong những ngày lễ, ngày rằm thường có rất nhiều người đến khẩn cầu.</p>\r\n<h3>Nhà thờ tộc Trần (Số 21. Lê Lợi – thị xã Hội An)</h3>\r\n<p style="text-align: justify;">Do một vị quan họ Trần (một dòng họ lớn từ Trung Hoa di cư đến Hội An vào những năm 1700) xây dựng năm 1802 theo những nguyên tắc phong thuỷ truyền thống của người Trung Hoa và người Việt. Tạo lạc trên một khu đất rộng khoảng 1500 m2, có nhiều hạng mục: nhà thờ tự ông bà và trưng bày các di vật liên quan đến dòng họ, nhà ở … Đây là nơi tụ họp con cháu vào dịp lễ bái, tri ân tổ tiên và giải quyết những vấn đề trong dòng tộc.</p>\r\n<p style="text-align: justify;">Nhà thờ tộc Trần là một trong những điểm tham quan điểm tham quan được nhiều du khách quan tâm.</p>\r\n<h3>Bảo tàng Lịch sử – Văn hóa</h3>\r\n<p style="text-align: justify;">Được thành lập vào năm 1989, bảo tàng trưng bày 212 hiện vật gốc và tư liệu có giá trị bằng gốm, sứ, đồng sắt, giấy, gỗ…phản ánh các giai đoạn phát triển của đô thị- thương cảng Hội An từ thời kỳ văn hoá Sa Huỳnh (từ thế kỷ thứ 2 sau Công Nguyên) đến thời kỳ văn hoá Chăm (từ thế kỷ 2 đến thế kỷ 15) và văn hoá Đại Việt, Đại Nam (từ thế kỷ 15 đến thế kỷ 19). Đến thăm Bảo tàng Lịch sử – Văn hoá Hội An, du khách sẽ có được cái nhìn tổng quát về tiến trình lịch sử cũng như bề dày văn hoá của đô thị cổ.</p>\r\n<h3>Bảo tàng gốm sứ Mậu Dịch (Số 80. Trần Phú – thị xã Hội An)</h3>\r\n<p style="text-align: justify;">Được xây dựng vào năm 1995, bảo tàng lưu giữ trên 430 hiện vật gốm sứ có niên đại từ thế kỷ 8 đến thế kỷ 18. Hầu hết các hiện vật là gốm sứ mậu dịch có nguồn gốc từ Trung Cận Đông, Ấn Độ, Trung Hoa, Nhật Bản, Thái Lan, Việt Nam … minh chứng cho vai trò quan trọng của thương cảng Hội An trong mạng lưới mậu dịch gốm sứ trên biển vào các thế kỷ trước, đồng thời cũng cho thấy quan hệ giao lưu văn hoá- kinh tế quốc tế đã từng diễn ra rất mạnh mẽ ở Hội An.</p>\r\n<h3>Bảo tàng văn hóa Sa Huỳnh (Số 149. Trần Phú – thị xã Hội An)</h3>\r\n<p style="text-align: justify;">Bảo tàng là nơi cung cấp những thông tin phong phú về cư dân cổ thuộc hệ văn hoá Sa Huỳnh – chủ nhân cảng thị Hội An sơ khai từng có quan hệ giao lưu Trung Hoa, Ấn Độ và các quốc gia Đông Nam Á. Tại đây trưng bày 216 hiện vật văn hoá Sa Huỳnh có niên đại trên dưới 2000 năm được phát hiện qua các đợt khảo sát, khai quật khảo cổ học tại các địa điểm: Hậu Xá, Thanh Chiêm, An Bàng, Xuân Lâm … từ năm 1989 đến năm 1994.</p>\r\n<p style="text-align: justify;">Các hiện vật tại bảo tàng được đánh giá là bộ sưu tập độc đáo nhất của Việt Nam hiện nay về văn hoá Sa Huỳnh.</p>', '', 1, 1, 1, '2009-08-15 00:37:35'),
(8, ' Bánh tráng cuốn thịt heo', '', '-banh-trang-cuon-thit-heo', '', '<p style="text-align: justify;"><img src="images/stories/banh-cuon-thit-heo.JPG" width="320" height="230" alt="Bánh tráng cuốn thịt heo" style="float: left; margin-right: 5px; margin-bottom: 5px;" />Du khách đến thành phố Đà Nẵng đi ngắm và tắm biển, tham quan: Ngũ Hành Sơn-Non nước, Bảo tàng điêu khắc Chămpa, cầu quay sông Hàn, cáp treo Bà Nà - Suối Mơ, v.v... hãy ghé thưởng thức món ăn ngon và rất độc đáo ở đây: bánh tráng cuốn thịt heo.</p>\r\n<p style="text-align: justify;">Một mâm bày biện nhìn vào là thấy hứng thú: rau sạch các loại, bánh đa (2 loại khô, ướt), thịt heo luộc được cắt đặc biệt rất lạ và bí ẩn, nước chấm, ớt, tỏi và các gia vị khác.  Người ăn tự cuốn cho mình những chiếc nem (hay còn gọi là gỏi - miền Nam) với các thành phần chủ yếu trên đây, gia giảm số lượng từng loại tuỳ ý rồi chấm vào nước mắm nêm đất Quảng thơm tho, gởi thèm rồi đưa vào "cửa khẩu"....</p>\r\n<p style="text-align: justify;">Đây là  một món nem cuốn bình dân nhưng bí quyết chính là ở đĩa thịt heo luộc. Thịt heo là loại thịt được lấy từ con heo nặng khoảng 70 kg, chỉ lấy khoảng 5 kg thịt mông ngon, sau đó chỉ lấy ra khoảng 40% (của 5kg) đạt chất lượng và tiêu chuẩn để luộc làm cho món bánh tráng cuốn thịt heo.</p>\r\n<p style="text-align: justify;">Khi luộc thịt phải tuân thủ các quy định và quy trình bắt buộc. Để giữ hương vị thơm, ngon và đảm bảo vệ sinh thực phẩm các dụng cụ đều phải sạch, nhúng qua nước sôi trước khi động vào miếng thịt. Khi luộc cũng phải chú ý giữ lửa sao cho không chín nhanh quá, không chậm quá, theo dõi chặt chẽ. Thịt luộc ra phải có phần mỡ thật trong, mỡ có màu sắc khác là không đạt yêu cầu, phần nạc tươi hơi sẩm có ở cả 2 phía miếng thịt mới lạ.  Bánh đa nướng, và bánh mì ướt làm bằng bột gạo dùng để gói do quán tự làm để kiểm soát được chất lượng. Những thứ gia vị khác như rau sống, hành sống, chuối chát, tỏi, ớt, và đặc biệt là nước chấm là bát mắm nêm thơm và cay, chấm chiếc nem đặc biệt này vào thì ai cũng thấy rất ngon miệng.</p>\r\n<p style="text-align: justify;">Bánh tráng cuốn thịt heo Đà Nẵng ai ăn mà không khen ngon mới thấy là lạ.</p>\r\n<p style="text-align: justify;">Đà Nẵng có những quán phục vụ bánh tráng cuốn thịt heo nổi tiếng là quán Mậu, quán Trần,... Nếu đến thăm Tp. Đà Nẵng, ngoài việc thưởng thức các đặc sản biển tươi, ngon, bổ dưỡng bạn hãy đến thưởng thức món "bánh tráng cuốn thịt heo", bạn sẽ thấy nó hấp dẫn và thú vị đến mức nào.</p>\r\n<p> </p>', '', 1, 1, 1, '2009-08-10 11:04:43'),
(9, 'Đã thèm tô mì Tuý Loan', '', 'da-them-to-mi-tuy-loan', '', '<p style="text-align: justify;"><img src="images/stories/my-quang.jpg" width="320" height="230" alt="Đã thèm tô mì Tuý Loan" style="float: left; margin-right: 5px; margin-bottom: 5px;" />Ở đôi bờ sông Tuý, huyện Hoà Vang, Đà Nẵng, dọc QL14B cũ và QL14B mới, rồi cả những đường thôn ngõ xóm, quán ăn nào cũng treo bảng bán mì Quảng. Mì Quảng quán nào cũng ngon, nhưng hơn hết là mì Bà Tỉnh ở gần cầu Giăng - ''linh hồn'' của mì Quảng Tuý Loan.</p>\r\n<p style="text-align: justify;">Mì Bà Tỉnh có sức hút không chỉ vì quán có truyền thống gần 100 năm, với bí quyết riêng về các công đoạn làm bánh tráng, tráng mì... mà còn ở chất lượng và giá hợp túi tiền với đủ loại khách hàng. Tô mì ngon nhờ nhưn chế biến từ thịt mông con bò vừa tơ. Nước lèo cũng hầm từ xương bò này và nêm đủ loại gia vị vừa ngọt vừa thơm. Đậu phộng (lạc) chọn mẻ không để lâu trên một mùa, rang lửa than vừa đủ độ để giữ chất bùi bùi, mằn mặn của đậu.</p>\r\n<p style="text-align: justify;">Rau sống dùng kèm phải chín loại, từ cây cải non đương nụ, cọng giá trắng tinh, rau muống non xanh chẻ nhỏ, lá hành chọn nơi gần củ có màu đậm, xà lách chọn lá xanh lợt… cho đến ngò ta xắt dài để ngọn, tất cả trộn đều với bắp chuối chát xắt mỏng như tơ - thấy đã thèm.</p>\r\n<p style="text-align: justify;">Sợi mì mỏng làm từ gạo xiệc chính gốc Đại Hiệp - Đại Lộc - Quảng Nam, ăn vừa có độ dai, vừa bùi mới ngon. Tô mì Bà Tỉnh chỉ 7.000đ cùng cái bánh tráng đặc sản vùng quê bên bờ sông Tuý. Cũng từ lò bánh tráng nhà bà Tỉnh mà ra, thơm lựng, giòn tan. Khách ở xa, sau khi ăn mì thường mua vài chục bánh tráng mang về làm quà.</p>\r\n<p style="text-align: justify;">Tô mì ngon và điệu nghệ như vậy, nên khách đông. Người dân xa xứ về thăm quê không quên đến Tuý Loan thưởng tô mì Bà Tỉnh. Ngày lễ tết, người dân dưới phố, bà con khắp nơi ngang qua Tuý Loan, đi viếng mộ ở nghĩa trang Gò Cà; thăm viếng người thân, bạn bè… vẫn để bụng ăn tô mì Quảng. Theo anh Nguyễn Đại Minh - truyền nhân đời thứ ba, hiện là chủ quán mì Bà Tỉnh cho biết, bình quân mỗi ngày bán 150 tô, chủ nhật lên đến 600 tô, còn những ngày tết thì trên cả ngàn là thường. ''Nhà mình một năm chỉ nghỉ bán ngày ba mươi để rước ông bà, còn lại bán suốt''.</p>\r\n<p> </p>', '', 1, 1, 1, '2011-03-01 06:43:38'),
(10, 'Thông tin phòng nghỉ Pearl Sea Hotel', '', 'thong-tin-phong-nghi-pearl-sea-hotel', '', '<p style="text-align: justify;">Khách sạn Pearl Sea là một trong những khách sạn mới bên cạnh biển Mỹ Khê, khai trương vào tháng 04 năm 2011, được thiết kế theo tiêu chuẩn tương đương 3 sao gồm 10 tầng - 45 phòng cùng những trang thiết bị hiện đại, sang trọng và ấm cúng. Với đội ngũ nhân viên trẻ trung, năng động và nhiệt tình, Khách sạn Pearl Sea tự tin sẽ mang lại cho Quý khách sự hài lòng cao nhất.<br /><br />Hệ thống phòng được phân làm 05 loại với các tiện nghi như sau:</p>\n<table border="0" style="width: 100%;">\n<tbody>\n<tr>\n<td>\n<h4>STANDARD ROOM</h4>\n</td>\n<td style="text-align: right;"><a href="index.php?option=com_ckforms&view=ckforms&id=1&Itemid=84"><img style="border-width: 0px;" alt="Booking" src="images/stories/booking_icon.gif" /> </a></td>\n</tr>\n</tbody>\n</table>\n<table border="0" style="width: 100%;">\n<tbody>\n<tr>\n<td>\n<ul class="checklist">\n<li>Phòng có giường đôi (1.6m)</li>\n<li>Truyền hình cáp</li>\n<li>Máy lạnh điều hòa </li>\n<li>Điện thoại quốc tế </li>\n<li>Internet tốc độ cao (ADSL) </li>\n<li>Nước nóng </li>\n<li>Tủ lạnh</li>\n<li>Máy sấy tóc</li>\n</ul>\n</td>\n<td width="300"><a title="Pearl Sea Hotel - Standard Room" href="images/stories/P4_L.jpg" rel="gb_imageset[p]"><img width="300" height="200" alt="Pearl Sea Hotel - Standard Room" src="images/stories/P4_S.jpg" /> </a><br />Click vào hình để xem ảnh lớn</td>\n</tr>\n</tbody>\n</table>\n<table border="0" style="width: 100%;">\n<tbody>\n<tr>\n<td>\n<h4>SUPERIOR ROOM</h4>\n</td>\n<td style="text-align: right;"><a href="index.php?option=com_ckforms&view=ckforms&id=1&Itemid=84"><img style="border-width: 0px;" alt="Booking" src="images/stories/booking_icon.gif" /> </a></td>\n</tr>\n</tbody>\n</table>\n<table border="0" style="width: 100%;">\n<tbody>\n<tr>\n<td>\n<ul class="checklist">\n<li>Phòng có King-size (1.8m)</li>\n<li>Truyền hình cáp</li>\n<li>Máy lạnh điều hòa </li>\n<li>Đi